package com.hobby.personalchat.ui\n\nimport androidx.compose.foundation.layout.padding\nimport androidx.compose.foundation.lazy.LazyColumn\nimport androidx.compose.material.icons.Icons\nimport androidx.compose.material.icons.filled.Chat\nimport androidx.compose.material.icons.filled.Person\nimport androidx.compose.material3.*\nimport androidx.compose.runtime.*\nimport androidx.compose.ui.Modifier\nimport androidx.compose.ui.tooling.preview.Preview\nimport com.hobby.personalchat.ui.composables.ChatItem\nimport com.hobby.personalchat.ui.theme.PersonalchatTheme\n\n@OptIn(ExperimentalMaterial3Api::class)\n@Composable\nfun HomeScreen() {\n    var selectedScreen by remember { mutableStateOf(Screen.Chats) }\n\n    Scaffold(\n        bottomBar = {\n            BottomAppBar {\n                NavigationBarItem(\n                    icon = { Icon(Icons.Filled.Chat, contentDescription = \"Chats\") },\n                    label = { Text(\"Chats\") },\n                    selected = selectedScreen == Screen.Chats,\n                    onClick = { selectedScreen = Screen.Chats }\n                )\n                NavigationBarItem(\n                    icon = { Icon(Icons.Filled.Person, contentDescription = \"Profile\") },\n                    label = { Text(\"Profile\") },\n                    selected = selectedScreen == Screen.Profile,\n                    onClick = { selectedScreen = Screen.Profile }\n                )\n            }\n        }\n    ) {\ innerPadding ->\n        when (selectedScreen) {\n            Screen.Chats -> {\n                LazyColumn(modifier = Modifier.padding(innerPadding)) {\n                    items(10) { // Replace with your actual chat data\n                        ChatItem()\n                    }\n                }\n            }\n            Screen.Profile -> {\n                // Replace with your Profile screen content\n                Text(text = \"Profile Screen\", modifier = Modifier.padding(innerPadding))\n            }\n        }\n    }\n}\n\nenum class Screen {\n    Chats,\n    Profile\n}\n\n@Preview(showBackground = true)\n@Composable\nfun HomeScreenPreview() {\n    PersonalchatTheme {\n        HomeScreen()\n    }\n}